<style lang="sass" rel="scss" scoped>
    $idea-width: 435px;

    #background {
        position: relative;
        z-index: 1;
        flex: 1;
        background-color: #333;
        background-position: 50%;
        background-size: cover;
        background-repeat: no-repeat;
    }

    #idea {
        width: $idea-width;
        height: 65px;
        position: absolute;
        left: 50%;
        top: 50%;
        margin-left: -$idea-width / 2;
        margin-top: -100px;
        background-image: url(data:image/png;base64,);
    }

    #banner-card {
        display: block;
        position: fixed;
        width: 200px;
        height: 50px;
        bottom: 38px;
        right: 8px;
        border-radius: 4px;
        background-color: rgba(23,24,25,.3);

        .uface {
            float: left;
            width: 40px;
            height: 40px;
            margin: 5px 8px 0 10px;
        }

        >div {
            margin-top: 8px;
            float: left;
        }
    }

    #banner-other {
        div {
            height: 18px;
            width: 132px;
        }

        span {
            color: #fff;
            font-size: 12px;
        }

        a {
            font-size: 12px;
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
        }
    }
</style>

<template v-cloak>
    <div id="background" ref="bg">
        <div id="idea"></div>
        <search></search>
        <div id="banner-card">
            <router-link class="uface" :to="uHome"><img :src="avatar"/></router-link>
            <div id="banner-other">
                <div class="oneline">
                    <span>作者：<router-link class="white-link" :to="uHome">{{ uName }}</router-link></span>
                </div>
                <div class="oneline">
                    <span>番剧：<router-link class="white-link" :to="bHome">{{ bName }}</router-link></span>
                </div>
            </div>
        </div>
    </div>
</template>

<script>

    import search from './search.vue'

    export default {
        components: {
            search
        },
        data () {
            return {
                avatar : "",
                uHome : "",
                uName : "",
                bHome : 0,
                bName : "",
                href : ""
            }
        },
        beforeCreate () {
            this.$root.$data.load = false
        },
        created () {
            this.$http.get('/api/background').then(function (res) {
                this.bannerFormat(res.body.data);
                this.$refs.bg.style.backgroundImage = 'url(' + this.href + ')';
            });
        },
        methods: {
            bannerFormat (banner) {
                this.avatar =  banner['avatar'];
                this.uName = banner['uName'];
                this.uHome = banner['uHome'] === null ? '#' : '/people/' + banner['uHome'];
                this.bHome = banner['bHome'] === null ? '#' : '/bangumi/' + banner['bHome'];
                this.bName = banner['bName'];
                this.href = banner['href'];
            }
        },
        destroyed () {
            this.$root.$data.load = true
        }
    }
</script>