<style lang="sass" rel="scss" scoped>
    $item-width: 300px;
    $pos-top-down: 12px;
    $pos-rig-left: 12px;
    $icon-size: 14px;

    @mixin transition ($opacity, $visibility, $left) {
        opacity: $opacity;
        visibility: $visibility;
        left: $left;
    }

    @mixin theme ($bg, $color, $border, $data) {
        background-color: $bg;
        color: $color;
        border: 1px solid $border;

        &:hover {
            transition-duration: 0s;
            opacity: 1;
        }

        .alert-icon {
            background-image: $data;
        }
    }

    .alert-warp-left-top {
        left: $pos-rig-left;
        top: $pos-top-down;
    }

    .alert-warp-right-top {
        right: $pos-rig-left;
        top: $pos-top-down;
    }

    .alert-warp-right-bottom {
        right: $pos-rig-left;
        bottom: $pos-top-down;
    }

    .alert-warp-left-bottom {
        left: $pos-rig-left;
        bottom: $pos-top-down;
    }

    .alert-warp {
        position: fixed;
        pointer-events: none;
    }

    .alert-item {
        padding: 15px 10px;
        font-size: 14px;
        font-weight: 700;
        font-family: sans-serif;
        line-height: 1.1;
        text-align: center;
        border-radius: 4px;
        position: relative;
        margin-bottom: 10px;
        pointer-events: auto;
        width: $item-width;
        cursor: pointer;
        transition-duration: .4s;
        display: flex;
        @include transition(.8, visible, 0)
    }

    .alert-content {
        word-wrap: break-word;
        flex: 1;
    }

    .alert-icon {
        width: $icon-size;
        height: $icon-size;
        background-size: $icon-size;
        background-position: center;
        background-repeat: no-repeat;
        margin: 0 5px;
    }

    .alert-auto {
        width: $icon-size;
        height: $icon-size;
        margin: 0 5px;
    }

    .theme-info {
        @include theme(#d9edf7, #31708f, #bce8f1, url(data:image/png;base64,))
    }

    .theme-error {
        @include theme(#f2dede, #a94442, #ebccd1, url(data:image/png;base64,))
    }

    .theme-success {
        @include theme(#dff0d8, #3c763d, #d6e9c6, url(data:image/png;base64,))
    }

    .theme-warning {
        @include theme(#fcf8e3, #8a6d3b, #faebcc, url(data:image/png;base64,))
    }

    .right-top-enter-active, .right-bottom-enter-active, .right-top-leave-active, .right-bottom-leave-active {
        @include transition(0, hidden, $item-width)
    }

    .left-top-enter-active, .left-bottom-enter-active, .left-top-leave-active, .left-bottom-leave-active {
        @include transition(0, hidden, -$item-width)
    }

    .alert-fade-func-linear {
        transition-timing-function: linear;
    }

    .alert-fade-func-ease {
        transition-timing-function: ease;
    }

    .alert-fade-func-ease-in {
        transition-timing-function: ease-in;
    }

    .alert-fade-func-ease-in-out {
        transition-timing-function: ease-in-out;
    }
</style>

<template>
    <div :class="['alert-warp', 'alert-warp-' + position]" :style="{ zIndex : index }">
        <transition-group :name="position">
            <div :class="['alert-item', 'theme-' + item.theme, 'alert-fade-func-' + easing]" v-for="item in list" @click="close(item)" :key="item.time">
                <span :class="['alert-icon', 'icon-' + item.theme]" v-if="icon"></span>
                <span class="alert-content">{{ item.content }}</span>
                <span class="alert-auto" v-if="!item.auto">&times;</span>
            </div>
        </transition-group>
    </div>
</template>

<script>

    export default {
        data () {
            return {
                theme : ['success', 'warning', 'error', 'info'],
                posArr : ['left-top', 'left-bottom', 'right-top', 'right-bottom'],
                fadeArr : ['linear', 'ease', 'ease-in', 'ease-in-out'],
                position : "right-bottom",
                easing : "ease",
                list : [],
                maxLength : 8,
                index : 999999,
                timeOut : 5000,
                timer : null,
                icon : true
            }
        },
        watch: {
            'list' (val) {
                if (val.length === 0) {
                    clearInterval(this.timer);
                    this.timer = null;
                }
            }
        },
        methods: {
            setTimer () {
                var vm = this;
                this.timer = setInterval(function () {
                    var temp = null;
                    for (var i=0; i<vm.list.length; i++) {
                        if (vm.list[i].auto) {
                            temp = vm.list[i];
                            break
                        }
                    }
                    if (temp === null) return;
                    if ((new Date().getTime() - temp.time) > vm.timeOut) {
                        vm.list.splice(i, 1)
                    }
                }, 400)
            },
            init (obj) {
                if (obj === undefined) return;

                if (this.fadeArr.indexOf(obj.transition) !== -1) {
                    this.easing = obj.easing
                }

                if (this.posArr.indexOf(obj.position) !== -1) {
                    this.position = obj.position
                }

                if (typeof obj.timeOut === 'number' && obj.timeOut > 0) {
                    this.timeOut = obj.timeOut
                }

                if (typeof obj.maxLength === 'number' && obj.maxLength > 0) {
                    this.maxLength = obj.maxLength
                }

                if (typeof obj.index === 'number' && obj.index > 0) {
                    this.index = obj.index
                }

                if (typeof obj.icon === 'boolean') {
                    this.icon = obj.icon
                }
            },
            open (obj) {
                if (obj === undefined) {
                    obj = {}
                }

                if (this.theme.indexOf(obj.theme) === -1) {
                    obj.theme = 'info'
                }

                if (obj.auto || obj.auto === undefined) {
                    obj.auto = true
                }

                if (obj.content === undefined) {
                    obj.content = 'undefinedï¼'
                }

                obj.time = new Date().getTime();

                this.list.push(obj);

                if (obj.auto && this.timer === null) {
                    this.setTimer()
                }

                if (this.list.length > this.maxLength) {
                    this.list.shift()
                }
            },
            close (item) {
                var index = this.list.indexOf(item);
                this.list.splice(index, 1)
            }
        }
    }
</script>